import React, { useState, useEffect } from "react";
import Link from 'next/link'
import { Button, Table } from 'semantic-ui-react'
import axios from "axios"

export default video;

function video(props) {

  const [list, setArray] = useState([]);
  const CancelToken = axios.CancelToken;
  const source = CancelToken.source();
  var str = ""

  useEffect(() => {
      axios.get("http://10.0.0.142:8007/footage/").then((responseGet) => setArray(responseGet.data));
  }, []);

  console.log(list.url);

  var contentsDates = new Array();
  var contentsTime = new Array();

  list.map(e => {
    var a = new Date(e.date);
    var month=a.getUTCMonth() + 1
    var day=a.getDate()
    var year=a.getFullYear()
    var finalDate = month+ "/" + day + "/" + year
    contentsDates.push(finalDate);

    var hours = a.getUTCHours();
    var ampm = hours >= 12 ? 'pm' : 'am';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    var minute = a.getMinutes();
    var timezone = a.getTimezoneOffset();

    var finalTime = hour + ":" +minute +ampm + " " + timezone
    console.log(finalTime);

  })


    return (

      <div class=" ui clearing segment">
        <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
        <h1 class="ui header">WelcomeIN</h1>
        <div class="ui inverted menu">
        <Link href="/index">
          <a class="item">
            Home
          </a>
          </Link>
          <Link href="/users">
            <a className="item">
              Authorized Individuals
          </a>
          </Link>
          <Link href="/footage">
            <a className="item">
              Footage</a>
          </Link>
          <Link href="/settings">
            <a className="item">
               Settings
          </a>
          </Link>
        </div>

        <h2 align="center">Video Activity Logs</h2>
        <div>

        <table class="ui selectable celled table">
        <thead>
          <tr>
            <th class="">Date</th>
            <th class="">Time</th>
            <th class="">Footage</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>4/11/20</td>
            <td>12:00pm</td>
            <td class="center aligned">
              <Button class="ui grey button" href='http://0.0.0.0:8007/footage/'>
              <video width="100" height="100" controls  > <source src= 'http://0.0.0.0:8007/footage/' type="video/mp4"></source></video>
              </Button>
            </td>
          </tr>
          {list.map(Element => (
            <td></td>
          ))}
          <tr>
            <td>4/11/20</td>
            <td>12:00pm</td>
            <td class="center aligned">
              <Button class="ui grey button" href='http://0.0.0.0:8007/footage/'>
              <video width="100" height="100" controls  > <source src= 'http://0.0.0.0:8007/footage/' type="video/mp4"></source></video>
              </Button>
            </td>
          </tr>
        </tbody>
      </table>
      </div>
    </div>



    );
}
