import { Button, Header, Image } from 'semantic-ui-react'
import React, { useState } from "react";
import Link from 'next/link'
import axios from "axios"

export default images;

function images(props) {
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [uploaded, setUploaded] = useState("");


    const handleSubmit = (evt) => {
        evt.preventDefault();
        var formData = new FormData();
        formData.append("FileField", image);
        formData.append("Name", name);
        console.log(image);
        axios.post("http://10.0.0.142:8007/friendlyfaces/", formData).then((responsePost) => axios.get("http://10.0.0.142:8007/friendlyfaces/").then((responseGet) => setUploaded(responseGet.data[0].Image)));
    }

    const onChangeImage = e =>{
        setImage(e.target.files[0]);
    }

    const onChangeName = e =>{
        setName(e.target.value)
    }

    return (

        <div >
        <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
        <h1 class="ui header"> WelcomeIN</h1>
        <div class="ui inverted menu">
        <Link href="/index">
          <a class="item">
            Home
          </a>
          </Link>
          <Link href="/users">
            <a className="item">
              Authorized Individuals
          </a>
          </Link>
          <Link href="/footage">
            <a className="item">
              Footage</a>
          </Link>
          <Link href="/settings">
            <a className="item">
              Settings
          </a>
          </Link>
        </div>

        <h2 align="center">Photo Activity Logs</h2>

        <div class="ui relaxed divided list">
            <div class="item">
                <i class="large github middle aligned icon"></i>
                <div class="content">
                <a class="header">Semantic-Org/Semantic-UI</a>
                <div class="description">Updated 10 mins ago</div>
                </div>
            </div>
            <div class="item">
                <i class="large github middle aligned icon"></i>
                <div class="content">
                <a class="header">Semantic-Org/Semantic-UI-Docs</a>
                <div class="description">Updated 22 mins ago</div>
                </div>
            </div>
            <div class="item">
                <i class="large github middle aligned icon"></i>
                <div class="content">
                <a class="header">Semantic-Org/Semantic-UI-Meteor</a>
                <div class="description">Updated 34 mins ago</div>
                </div>
            </div>
            </div>

        </div>



    );
}
;
