import { Button, Header, Image } from 'semantic-ui-react'
import React, { useState } from "react";
import Link from 'next/link'
import axios from "axios"

export default images;

function images(props) {
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [uploaded, setUploaded] = useState("");


    const handleSubmit = (evt) => {
        evt.preventDefault();
        var formData = new FormData();
        formData.append("FileField", image);
        formData.append("Name", name);
        console.log(image);
        axios.post("http://10.0.0.142:8007/friendlyfaces/", formData).then((responsePost) => axios.get("http://10.0.0.142:8007/friendlyfaces/").then((responseGet) => setUploaded(responseGet.data[0].Image)));
    }

    const onChangeImage = e =>{
        setImage(e.target.files[0]);
    }

    const onChangeName = e =>{
        setName(e.target.value)
    }

    return (

        <div >
        <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
        <h1 class="ui header"> WelcomeIN</h1>
        <div class="ui inverted menu">
          <a class="item">
            Home
    </a>
          <Link href="/users">
            <a className="item">
              Authorized Individuals
          </a>
          </Link>
          <Link href="/footage">
            <a className="item">
              Footage</a>
          </Link>
          <Link href="/settings">
            <a className="item">
              Settings
          </a>
          </Link>
        </div>

        <Header align="center">Upload A Photo of an Authorized Individual</Header>

            <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
            <form className="ui form segment" onSubmit={handleSubmit}>
                <label><h2>Your Face:</h2></label>
                <input type="file" onChange={onChangeImage} />

                <label>Your Name:
                <input type="text" value={name} onChange={onChangeName} />
                </label>
                <input type="submit" value="Submit" />
                <img src={uploaded} />
            </form>
        </div>



    );
}
;
