import { Button } from 'semantic-ui-react'
import React, { useState } from "react";
import Link from 'next/link'
import axios from "axios"

export default images;

function images(props) {
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [uploaded, setUploaded] = useState("");


    const handleSubmit = (evt) => {
        evt.preventDefault();
        var formData = new FormData();
        formData.append("FileField", image);
        formData.append("Name", name);
        console.log(image);
        axios.post("http://10.0.0.142:8007/friendlyfaces/", formData).then((responsePost) => axios.get("http://10.0.0.142:8007/friendlyfaces/").then((responseGet) => setUploaded(responseGet.data[0].Image)));
    }

    const onChangeImage = e =>{
        setImage(e.target.files[0]);
    }

    const onChangeName = e =>{
        setName(e.target.value)
    }

    return (
        <div>
        <Modal>
        <Modal.Header>Select a Photo</Modal.Header>
                <Modal.Content image>
                <Image wrapped size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' />
                    <Modal.Description>
                        <Header>Upload a photo of authorized individual</Header>
                        <p>
                        <form className="ui form">
                    <div className="field">
                        <label></label>
                        <input name="image" type="text" placeholder="image...">
                        </input>
                        <Divider horizontal></Divider>
                        <button class="small ui button">
                            Upload Image
                        </button>

                    </div>
                </form>
                        </p>
                    </Modal.Description>
                </Modal.Content>
        </Modal>
        <Divider horizontal></Divider>
        <div class="ui fitted divider"></div>
        <Divider horizontal></Divider>
    </div>

    );
}
